import type { Fn3 } from "@thi.ng/api";
import { peek } from "@thi.ng/arrays/peek";
import { isString } from "@thi.ng/checks/is-string";
import { delay } from "@thi.ng/compose/delay";
import { DEFAULT, defmulti } from "@thi.ng/defmulti";
import { EMOJI } from "@thi.ng/emoji/emoji";
import type { ILogger } from "@thi.ng/logger";
import type { ParseScope, ParseState } from "@thi.ng/parse";
import { defContext } from "@thi.ng/parse/context";
import { defGrammar } from "@thi.ng/parse/grammar";
import { escapeEntities } from "@thi.ng/strings/entities";
import type {
	ColumnAlign,
	MDParseContext,
	ParseOpts,
	TagTransforms,
	TodoAttribs,
} from "./api.js";

export const GRAMMAR = defGrammar(`
DNL1: <DNL>+ => discard ;
DNL2: <NL>{2,} ;
lbr: '\\\\'! <NL>! ;
esc: '\\\\'! ( '<' | '[' | '_' | '*' | '\`' | '~' | ':' ) ;
inlinedelim: ( "![" | '[' | "**" | '_' | '\`' | '<' | '\\\\' | "~~" | " :" | <lbr> ) ;
delim: ( <inlinedelim> | <DNL2> ) ;
delim1: ( <inlinedelim> | <NL> ) ;
body: .(?-<delim>!) => join ;
body1: .(?-<delim1>!) => join ;

ref: "[["! .(?+"]]"!) => join ;
fnref: "[^"! <label> ;
fnote: <LSTART> "[^"! <fnlabel> <WS1> <para> ;
fnlabel: .(?+"]:"!) => join ;
label: .(?+']'!) => join ;
target: .(?+')'!) => join ;
link: '['! <linklabel> '('! <target> ;
linkref: '['! <linklabel> '['! <label> ;
linkdef: <LSTART> '['! <label> ':'! <WS1> <ldtarget> ;
ldtarget: .(?+<DNL1>) => join ;
linklabel: (<img> | <bold> | <italic> | <strike> | <code> | <emoji> | <linkbody>)* ']'! ;
linkdelim: ( "![" | '[' | "**" | '_' | "~~" | '\`' | ']') ;
linkbody: .(?-<linkdelim>!) => join ;
img: "!["! <label> '('! <target> ;
bold: "**"! (<ref> | <italic> | <strike> | <code> | <emoji> | <body1>)* "**"! ;
italic: '_'! (<ref> | <bold> | <strike> | <code> | <emoji> | <body1>)* '_'! ;
strike: "~~"! (<ref> | <bold> | <italic> | <code> | <emoji> | <body1>)* "~~"! ;
code: '\`'! .(?+'\`'!) => join ;
kbd: "<kbd>"! .(?+"</kbd>"!) => join ;
emoji: ' '? ':'! <ALPHA_NUM>(?+':'!) => join ;
para: (<ref> | <img> | <fnref> | <linkref> | <link> | <bold> | <italic> | <strike> | <code> | <kbd> | <emoji> | <lbr> | <esc> | <body>)* <DNL2>! ;

hdlevel: '#'+ => count ;
hd: <LSTART> <hdlevel> <WS0>
    (<ref> | <img> | <fnref> | <link> | <bold> | <italic> | <strike> | <code> | <emoji> | <body1> )* <DNL1> ;

lilevel: ' '* => count ;
uint: <DIGIT>+ => int ;
ulid: <DNL> <WS0> '-'! ;
olid: <DNL> <WS0> <DIGIT>+! '.'! ;
lidelim: ( <delim> | <ulid> | <olid> ) ;
libody: .(?-<lidelim>!) => join ;
todo: '['! [ xX] ']'! <WS1> => hoistR ;
ulitem: <LSTART> <lilevel> "- "! <todo>?
        (<ref> | <img> | <fnref> | <link> | <bold> | <italic> | <strike> | <code> | <emoji> | <libody> )* <DNL> ;
olitem: <LSTART> <lilevel> <uint> ". "! <todo>?
        (<ref> | <img> | <fnref> | <link> | <bold> | <italic> | <strike> | <code> | <emoji> | <libody> )* <DNL> ;
list: (<ulitem> | <olitem>)+ <DNL1> ;

cbdelim: <LSTART> "\`\`\`"! ;
codeblock: <cbdelim>! <codemeta> <codebody> <DNL1> ;
codemeta: .(?+<NL>!) => join ;
codebody: .(?+<cbdelim>) => join ;

customdelim: <LSTART> ":::"! ;
customblock: <customdelim>! <custommeta> <custombody> <DNL1> ;
custommeta: .(?+<NL>!) => join ;
custombody: .(?+<customdelim>) => join ;

metablock: <LSTART> "{{{"! <metabody> <DNL1> ;
metabody: .(?+<metaend>!) => join ;
metaend: "}}}" <LEND> ;

bqline: <LSTART> "> "!
        (<ref> | <img> | <fnref> | <link> | <bold> | <italic> | <strike> | <code> | <emoji> | <bqlbr> | <body1>)* <DNL> ;
bqlbr: '\\\\'!(?-<DNL>) ;
bquote: <bqline>+ <DNL1> ;

tdelim: (<inlinedelim> | '|' ) ;
tbody: .(?-<tdelim>!) => join ;
tcell: <WS0> (<ref> | <img> | <fnref> | <link> | <bold> | <italic> | <strike> | <code> | <emoji> | <tbody> )* '|'! ;
trow: <LSTART> '|'! <tcell>(?+<DNL>) ;
table: <trow>+ <DNL1> ;

hr: "--" ('-'(?-<NL>!))* <DNL1> => join ;

main: <WS0> (<hd> | <list> | <bquote> | <codeblock> | <customblock> | <metablock> | <table> | <hr> | <fnote> | <linkdef> | <para>)* ;
`);

export class ParseError extends Error {
	constructor(public state?: ParseState<string>) {
		super(
			state
				? `stopped line: ${state.l}, col: ${state.c} (pos: ${state.p})`
				: undefined
		);
	}
}

export const parseRaw = (src: string) => {
	const ctx = defContext(src + "\n\n");
	if (!GRAMMAR!.rules.main(ctx)) throw new ParseError(ctx.state);
	return ctx;
};

export const parse = (
	src: string,
	{
		tags,
		opts,
		logger,
	}: Partial<{
		tags: Partial<TagTransforms>;
		opts: Partial<ParseOpts>;
		logger: ILogger;
	}> = {}
) => {
	const parseCtx = parseRaw(src);
	const mdCtx = defParseContext(tags, opts, logger);
	const result: any[] = [];
	walk(parseCtx.root, mdCtx, result);
	return { result, ctx: mdCtx, complete: parseCtx.done };
};

export const DEFAULT_TAG_TRANSFORMS: TagTransforms = {
	bold: (_, body) => ["strong", {}, ...body],
	blockquote: (_, body, meta) => [
		"blockquote",
		__withMeta({}, meta),
		...body,
	],
	br: () => ["br", {}],
	code: (_, body) => ["code", {}, body],
	codeblock: (_, lang, __head, body, meta) => [
		"pre",
		__withMeta({ data: { lang }, __head }, meta),
		["code", {}, body],
	],
	custom: (_, kind, body, meta) => [kind, __withMeta({}, meta), body],
	emoji: (_, id) => EMOJI[id] || id,
	footnote: (_, id, body, meta) => [
		"li",
		__withMeta({ id: `fn-${id}` }, meta),
		["sup", {}, `[${id}] `],
		...body,
		" ",
		["a", { href: `#fnref-${id}` }, "↩︎"],
	],
	footnoteRef: (_, id) => [
		"sup",
		{},
		["a", { id: `fnref-${id}`, href: `#fn-${id}` }, `[${id}]`],
	],
	footnoteWrapper: (_, notes) => [
		"ul",
		{ id: "footnotes" },
		...Object.keys(notes)
			.sort()
			.map((id) => notes[id]),
	],
	heading: (_, level, body, meta) => [
		level > 6 ? "p" : `h${level}`,
		__withMeta({}, meta),
		...body,
	],
	hr: (_, __length) => ["hr", { __length }],
	img: (_, alt, src) => ["img", { src, alt }],
	italic: (_, body) => ["em", {}, ...body],
	kbd: (_, key) => ["kbd", {}, key],
	link: (_, href, body) => ["a", { href }, ...body],
	linkRef: (ctx, refID, body) => [
		"a",
		{ href: delay(() => ctx.linkRefs[refID]) },
		...body,
	],
	meta: (_, body) => body,
	olitem: (_, attribs, index, body) => [
		"li",
		{ ...attribs, __index: index },
		...body,
	],
	ol: (_, items, meta) => ["ol", __withMeta({}, meta), ...items],
	para: (_, body, meta) => ["p", __withMeta({}, meta), ...body],
	strike: (_, body) => ["s", {}, ...body],
	table: (_, __align, head, rows, meta) => [
		"table",
		__withMeta({ __align }, meta),
		["thead", {}, head],
		["tbody", {}, ...rows],
	],
	tableCell: (_, body) => ["td", {}, ...body],
	tableRow: (_, cells) => ["tr", {}, ...cells],
	ul: (_, items, meta) => ["ul", __withMeta({}, meta), ...items],
	ulitem: (_, attribs, body) => ["li", attribs, ...body],
};

export const defParseContext = (
	tags?: Partial<TagTransforms>,
	opts?: Partial<ParseOpts>,
	logger?: ILogger
): MDParseContext => ({
	footnotes: {},
	headings: [],
	linkRefs: {},
	hasFootnotes: false,
	logger,
	meta: null,
	opts: {
		escape: false,
		...opts,
	},
	tags: { ...DEFAULT_TAG_TRANSFORMS, ...tags },
});

export const walk: Fn3<
	ParseScope<string>,
	MDParseContext,
	any[],
	void
> = defmulti<ParseScope<string>, MDParseContext, any[], void>(
	(x, ctx) => {
		ctx.logger && ctx.logger.debug(x);
		return x.id;
	},
	{
		body1: "body",
		bqline: "repeat0",
		bqlbr: "lbr",
		label: "body",
		libody: "body",
		linkbody: "body",
		main: "root",
		repeat1: "repeat0",
		tbody: "body",
	},
	{
		[DEFAULT]: (scope: ParseScope<string>, ctx: MDParseContext) => {
			throw new Error(
				`unknown ID: ${scope.id}, ctx: ${JSON.stringify(ctx)}`
			);
		},

		root: ({ children }, ctx, acc) => {
			if (!children) return;
			walk(children![0], ctx, acc);
			if (ctx.hasFootnotes) {
				__collect(acc, ctx.tags.footnoteWrapper(ctx, ctx.footnotes));
			}
		},

		main: (scope, ctx, acc) => walk(scope.children![0], ctx, acc),

		repeat0: (scope, ctx, acc) =>
			scope.children && __children(ctx, scope.children, acc),

		body: (scope, ctx, acc) => __collect(acc, __escape(ctx, scope.result)),

		bold: (scope, ctx, acc) =>
			__collect(
				acc,
				ctx.tags.bold(ctx, __children(ctx, scope.children!))
			),

		bquote: (scope, ctx, acc) => {
			const body: any[] = [];
			const children = scope.children![0].children!;
			for (let i = 0, n = children!.length - 1; i <= n; i++) {
				const bqline = children[i].children![0];
				walk(bqline, ctx, body);
				if (i < n) {
					if (!bqline.children) {
						__collect(body, ctx.tags.br(ctx));
						__collect(body, ctx.tags.br(ctx));
					} else if (peek(bqline.children!).id !== "bqlbr") {
						body.push(" ");
					}
				}
			}
			__collect(
				acc,
				ctx.tags.blockquote(ctx, __trimBody(body), ctx.meta)
			);
			ctx.meta = null;
		},

		code: (scope, ctx, acc) =>
			__collect(acc, ctx.tags.code(ctx, __escape(ctx, scope.result))),

		codeblock: ({ children }, ctx, acc) => {
			const [lang, ...head] = children![0].result.split(" ");
			const body = children![1].result.trim();
			__collect(
				acc,
				ctx.tags.codeblock(
					ctx,
					lang,
					head,
					__escape(ctx, body),
					ctx.meta
				)
			);
			ctx.meta = null;
		},

		customblock: ({ children }, ctx, acc) => {
			__collect(
				acc,
				ctx.tags.custom(
					children![0].result,
					children![1].result.trim(),
					ctx.meta
				)
			);
			ctx.meta = null;
		},

		emoji: ({ result }, ctx, acc) => {
			if (result[0] === " ") {
				__collect(acc, " ");
				result = result.substring(1);
			}
			__collect(acc, ctx.tags.emoji(ctx, result));
		},

		esc: (scope, ctx, acc) =>
			acc.push(__escape(ctx, scope.children![0].result)),

		fnote: ({ children }, ctx) => {
			const body: any[] = [];
			const id = children![0].result;
			walk(children![1].children![0], ctx, body);
			const res = ctx.tags.footnote(ctx, id, body, ctx.meta);
			if (res != null) {
				ctx.hasFootnotes = true;
				ctx.footnotes[id] = res;
			}
			ctx.meta = null;
		},

		fnref: (scope, ctx, acc) =>
			__collect(
				acc,
				ctx.tags.footnoteRef(ctx, scope.children![0].result)
			),

		hd: ({ children }, ctx, acc) => {
			const body: any[] = [];
			const level = children![0].result;
			walk(children![1], ctx, body);
			ctx.headings.push({ level, body });
			__collect(
				acc,
				ctx.tags.heading(ctx, level, __trimBody(body), ctx.meta)
			);
			ctx.meta = null;
		},

		hr: (scope, ctx, acc) =>
			__collect(acc, ctx.tags.hr(ctx, scope.result.length)),

		img: ({ children }, ctx, acc) =>
			__collect(
				acc,
				ctx.tags.img(
					ctx,
					__escape(ctx, children![0].result.trim()),
					__escape(ctx, children![1].result)
				)
			),

		italic: (scope, ctx, acc) =>
			__collect(
				acc,
				ctx.tags.italic(ctx, __children(ctx, scope.children!))
			),

		kbd: (scope, ctx, acc) =>
			__collect(acc, ctx.tags.kbd(ctx, scope.result)),

		lbr: (_, ctx, acc) => __collect(acc, ctx.tags.br(ctx)),

		link: ({ children }, ctx, acc) => {
			__collect(
				acc,
				ctx.tags.link(
					ctx,
					children![1].result,
					__children(ctx, children![0].children!)
				)
			);
		},

		linkdef: ({ children }, ctx) => {
			ctx.linkRefs[children![0].result] = children![1].result;
		},

		linkref: ({ children }, ctx, acc) => {
			const body: any[] = [];
			walk(children![0], ctx, body);
			__collect(acc, ctx.tags.linkRef(ctx, children![1].result, body));
		},

		list: (scope, ctx, acc) => {
			const children = scope.children![0].children!;
			const stack: any[][] = [
				[children[0].id === "ulitem" ? "ul" : "ol"],
			];
			const levels = [0];
			for (let item of children) {
				const currLevel = item.children![0].result;
				if (currLevel > peek(levels)) {
					const sublist = [item.id === "ulitem" ? "ul" : "ol"];
					const parent = peek(stack);
					parent.length > 1
						? peek(parent).push(sublist)
						: parent.push([
								parent[0] === "ul" ? "ulitem" : "olitem",
								{},
								sublist,
						  ]);
					stack.push(sublist);
					levels.push(currLevel);
				} else if (currLevel < peek(levels)) {
					while (currLevel < peek(levels)) {
						stack.pop();
						levels.pop();
					}
				}
				walk(item, ctx, peek(stack));
			}

			const $list = (root: any[], isRoot = false) =>
				ctx.tags[<"ul" | "ol">root[0]](
					ctx,
					root.slice(1).map($item),
					isRoot ? ctx.meta : null
				);

			const $item = (item: any[]) => {
				let last = item[item.length - 1];
				if (last[0] === "ul" || last[0] === "ol")
					item[item.length - 1] = $list(last);
				return item[0] === "ulitem"
					? ctx.tags.ulitem(ctx, item[1], item.slice(2))
					: ctx.tags.olitem(ctx, item[1], item[2], item.slice(3));
			};

			__collect(acc, $list(stack[0], true));
			ctx.meta = null;
		},

		metablock: ({ children }, ctx) => {
			ctx.meta = ctx.tags.meta(ctx, children![0].result.trim());
		},

		olitem: ({ children }, ctx, acc) => {
			const body: any[] = [];
			walk(children![3], ctx, body);
			__collect(acc, [
				"olitem",
				__listItemAttribs(children![2]),
				children![1].result,
				...__trimBody(body),
			]);
		},

		para: (scope, ctx, acc) => {
			__collect(
				acc,
				ctx.tags.para(
					ctx,
					__trimBody(__children(ctx, scope.children!)),
					ctx.meta
				)
			);
			ctx.meta = null;
		},

		strike: (scope, ctx, acc) =>
			__collect(
				acc,
				ctx.tags.strike(ctx, __children(ctx, scope.children!))
			),

		table: (scope, ctx, acc) => {
			const children = scope.children![0].children!;
			const head: any[] = [];
			const rows: any[] = [];
			walk(children[0], ctx, head);
			let align: ColumnAlign[];
			if (children.length > 1) {
				align = __columnAlignments(children[1].children![0].children!);
				for (let c of children.slice(2)) walk(c, ctx, rows);
			} else {
				align = new Array(
					children[0].children![0].children!.length
				).fill("left");
			}
			__collect(acc, ctx.tags.table(ctx, align, head[0], rows, ctx.meta));
			ctx.meta = null;
		},

		tcell: ({ children }, ctx, acc) =>
			__collect(
				acc,
				ctx.tags.tableCell(ctx, __trimBody(__children(ctx, children!)))
			),

		trow: ({ children }, ctx, acc) =>
			__collect(
				acc,
				ctx.tags.tableRow(ctx, __children(ctx, children![0].children!))
			),

		ulitem: ({ children }, ctx, acc) => {
			const body: any[] = [];
			walk(children![2], ctx, body);
			__collect(acc, [
				"ulitem",
				__listItemAttribs(children![1]),
				...__trimBody(body),
			]);
		},
	}
);

const __collect = (acc: any[], x: any) => x != null && acc.push(x);

const __children = (
	ctx: MDParseContext,
	children: ParseScope<string>[],
	acc: any[] = []
) => {
	for (let c of children!) walk(c, ctx, acc);
	return acc;
};

const __escape = (ctx: MDParseContext, x: string) =>
	ctx.opts.escape ? escapeEntities(x) : x;

const __withMeta = (target: any, meta?: any) => {
	if (meta != null) {
		target.__meta = meta;
	}
	return target;
};

const __listItemAttribs = (scope?: ParseScope<string>): TodoAttribs =>
	scope?.id === "todo"
		? {
				__todo: true,
				__done: scope.result === "x",
		  }
		: {};

const __trimBody = (body: any[]) => {
	if (body.length === 1 && isString(body[0])) body[0] = body[0].trim();
	return body;
};

const __columnAlignments = (children: ParseScope<string>[]) => {
	const align: ColumnAlign[] = [];
	for (let c of children) {
		const raw = <string>c.children![0].children![0].result.trim();
		const isLeft = raw.startsWith(":-") ? 1 : 0;
		const isRight = raw.endsWith("-:") ? 2 : 0;
		align.push(
			<ColumnAlign>(
				["default", "left", "right", "center"][isLeft | isRight]
			)
		);
	}
	return align;
};
